mapper.update:
update exam_title
set qid = #{qid,jdbcType=VARCHAR},
  title_no = #{titleNo,jdbcType=VARCHAR},
  title_name = #{titleName,jdbcType=VARCHAR}
where title_id = #{titleId,jdbcType=VARCHAR}

mapper.select:
